    59→type AttachmentsContext = {
    60→  files: (FileUIPart & { id: string })[];
    61→  add: (files: File[] | FileList) => void;
    62→  remove: (id: string) => void;
    63→  clear: () => void;
    64→  openFileDialog: () => void;
    65→  fileInputRef: RefObject<HTMLInputElement | null>;
    66→};
