    68→const AttachmentsContext = createContext<AttachmentsContext | null>(null);
    69→
    70→export const usePromptInputAttachments = () => {
    71→  const context = useContext(AttachmentsContext);
    72→
    73→  if (!context) {
    74→    throw new Error(
    75→      "usePromptInputAttachments must be used within a PromptInput"
    76→    );
    77→  }
    78→
    79→  return context;
    80→};
