   128→export type PromptInputAttachmentsProps = Omit<
   129→  HTMLAttributes<HTMLDivElement>,
   130→  "children"
   131→> & {
   132→  children: (attachment: FileUIPart & { id: string }) => React.ReactNode;
   133→};
   134→
   135→export function PromptInputAttachments({
   136→  className,
   137→  children,
   138→  ...props
   139→}: PromptInputAttachmentsProps) {
   140→  const attachments = usePromptInputAttachments();
   141→  const [height, setHeight] = useState(0);
   142→  const contentRef = useRef<HTMLDivElement>(null);
   143→
   144→  useLayoutEffect(() => {
   145→    const el = contentRef.current;
   146→    if (!el) {
   147→      return;
   148→    }
   149→    const ro = new ResizeObserver(() => {
   150→      setHeight(el.getBoundingClientRect().height);
   151→    });
   152→    ro.observe(el);
   153→    setHeight(el.getBoundingClientRect().height);
   154→    return () => ro.disconnect();
   155→  }, []);
   156→
   157→  return (
   158→    <div
   159→      aria-live="polite"
   160→      className={cn(
   161→        "overflow-hidden transition-[height] duration-200 ease-out",
   162→        className
   163→      )}
   164→      style={{ height: attachments.files.length ? height : 0 }}
   165→      {...props}
   166→    >
   167→      <div className="flex flex-wrap gap-2 p-3 pt-3" ref={contentRef}>
   168→        {attachments.files.map((file) => (
   169→          <Fragment key={file.id}>{children(file)}</Fragment>
   170→        ))}
   171→      </div>
   172→    </div>
   173→  );
   174→}
