   201→export type PromptInputMessage = {
   202→  text?: string;
   203→  files?: FileUIPart[];
   204→};
   205→
   206→export type PromptInputProps = Omit<
   207→  HTMLAttributes<HTMLFormElement>,
   208→  "onSubmit"
   209→> & {
   210→  // Variant selection
   211→  variant?: "default" | "glassmorphism";
   212→
   213→  // File attachment props
   214→  accept?: string; // e.g., "image/*" or leave undefined for any
   215→  multiple?: boolean;
   216→  // When true, accepts drops anywhere on document. Default false (opt-in).
   217→  globalDrop?: boolean;
   218→  // Render a hidden input with given name and keep it in sync for native form posts. Default false.
   219→  syncHiddenInput?: boolean;
   220→  // Minimal constraints
   221→  maxFiles?: number;
   222→  maxFileSize?: number; // bytes
   223→  onError?: (err: {
   224→    code: "max_files" | "max_file_size" | "accept";
   225→    message: string;
   226→  }) => void;
   227→
   228→  // Glassmorphism variant props
   229→  maxWidth?: number; // Max width for glassmorphism container (default: 900)
   230→
   231→  onSubmit: (
   232→    message: PromptInputMessage,
   233→    event: FormEvent<HTMLFormElement>
   234→  ) => void;
   235→};
   236→
   237→export const PromptInput = ({
   238→  className,
   239→  variant = "default",
   240→  accept,
   241→  multiple,
   242→  globalDrop,
   243→  syncHiddenInput,
   244→  maxFiles,
   245→  maxFileSize,
   246→  maxWidth = 900,
   247→  onError,
   248→  onSubmit,
   249→  ...props
   250→}: PromptInputProps) => {
   251→  const [items, setItems] = useState<(FileUIPart & { id: string })[]>([]);
   252→  const inputRef = useRef<HTMLInputElement | null>(null);
   253→  const anchorRef = useRef<HTMLSpanElement>(null);
   254→  const formRef = useRef<HTMLFormElement | null>(null);
   255→
   256→  // Find nearest form to scope drag & drop
   257→  useEffect(() => {
   258→    const root = anchorRef.current?.closest("form");
   259→    if (root instanceof HTMLFormElement) {
   260→      formRef.current = root;
   261→    }
   262→  }, []);
   263→
   264→  const openFileDialog = useCallback(() => {
   265→    inputRef.current?.click();
   266→  }, []);
   267→
   268→  const matchesAccept = useCallback(
   269→    (f: File) => {
   270→      if (!accept || accept.trim() === "") {
   271→        return true;
   272→      }
   273→      // Simple check: if accept includes "image/*", filter to images; otherwise allow.
   274→      if (accept.includes("image/*")) {
   275→        return f.type.startsWith("image/");
   276→      }
   277→      return true;
   278→    },
   279→    [accept]
   280→  );
   281→
   282→  const add = useCallback(
   283→    (files: File[] | FileList) => {
   284→      const incoming = Array.from(files);
   285→      const accepted = incoming.filter((f) => matchesAccept(f));
   286→      if (accepted.length === 0) {
   287→        onError?.({
   288→          code: "accept",
   289→          message: "No files match the accepted types.",
   290→        });
   291→        return;
   292→      }
   293→      const withinSize = (f: File) =>
   294→        maxFileSize ? f.size <= maxFileSize : true;
   295→      const sized = accepted.filter(withinSize);
   296→      if (sized.length === 0 && accepted.length > 0) {
   297→        onError?.({
   298→          code: "max_file_size",
   299→          message: "All files exceed the maximum size.",
   300→        });
   301→        return;
   302→      }
   303→      setItems((prev) => {
   304→        const capacity =
   305→          typeof maxFiles === "number"
   306→            ? Math.max(0, maxFiles - prev.length)
   307→            : undefined;
   308→        const capped =
   309→          typeof capacity === "number" ? sized.slice(0, capacity) : sized;
   310→        if (typeof capacity === "number" && sized.length > capacity) {
   311→          onError?.({
   312→            code: "max_files",
   313→            message: "Too many files. Some were not added.",
   314→          });
   315→        }
   316→        const next: (FileUIPart & { id: string })[] = [];
   317→        for (const file of capped) {
   318→          next.push({
   319→            id: nanoid(),
   320→            type: "file",
   321→            url: URL.createObjectURL(file),
   322→            mediaType: file.type,
   323→            filename: file.name,
   324→          });
   325→        }
   326→        return prev.concat(next);
   327→      });
   328→    },
   329→    [matchesAccept, maxFiles, maxFileSize, onError]
   330→  );
   331→
   332→  const remove = useCallback((id: string) => {
   333→    setItems((prev) => {
   334→      const found = prev.find((file) => file.id === id);
   335→      if (found?.url) {
   336→        URL.revokeObjectURL(found.url);
   337→      }
   338→      return prev.filter((file) => file.id !== id);
   339→    });
   340→  }, []);
   341→
   342→  const clear = useCallback(() => {
   343→    setItems((prev) => {
   344→      for (const file of prev) {
   345→        if (file.url) {
   346→          URL.revokeObjectURL(file.url);
   347→        }
   348→      }
   349→      return [];
   350→    });
   351→  }, []);
   352→
   353→  // Note: File input cannot be programmatically set for security reasons
   354→  // The syncHiddenInput prop is no longer functional
   355→  useEffect(() => {
   356→    if (syncHiddenInput && inputRef.current) {
   357→      // Clear the input when items are cleared
   358→      if (items.length === 0) {
   359→        inputRef.current.value = "";
   360→      }
   361→    }
   362→  }, [items, syncHiddenInput]);
   363→
   364→  // Attach drop handlers on nearest form and document (opt-in)
   365→  useEffect(() => {
   366→    const form = formRef.current;
   367→    if (!form) {
   368→      return;
   369→    }
   370→    const onDragOver = (e: DragEvent) => {
   371→      if (e.dataTransfer?.types?.includes("Files")) {
   372→        e.preventDefault();
   373→      }
   374→    };
   375→    const onDrop = (e: DragEvent) => {
   376→      if (e.dataTransfer?.types?.includes("Files")) {
   377→        e.preventDefault();
   378→      }
   379→      if (e.dataTransfer?.files && e.dataTransfer.files.length > 0) {
   380→        add(e.dataTransfer.files);
   381→      }
   382→    };
   383→    form.addEventListener("dragover", onDragOver);
   384→    form.addEventListener("drop", onDrop);
   385→    return () => {
   386→      form.removeEventListener("dragover", onDragOver);
   387→      form.removeEventListener("drop", onDrop);
   388→    };
   389→  }, [add]);
   390→
   391→  useEffect(() => {
   392→    if (!globalDrop) {
   393→      return;
   394→    }
   395→    const onDragOver = (e: DragEvent) => {
   396→      if (e.dataTransfer?.types?.includes("Files")) {
   397→        e.preventDefault();
   398→      }
   399→    };
   400→    const onDrop = (e: DragEvent) => {
   401→      if (e.dataTransfer?.types?.includes("Files")) {
   402→        e.preventDefault();
   403→      }
   404→      if (e.dataTransfer?.files && e.dataTransfer.files.length > 0) {
   405→        add(e.dataTransfer.files);
   406→      }
   407→    };
   408→    document.addEventListener("dragover", onDragOver);
   409→    document.addEventListener("drop", onDrop);
   410→    return () => {
   411→      document.removeEventListener("dragover", onDragOver);
   412→      document.removeEventListener("drop", onDrop);
   413→    };
   414→  }, [add, globalDrop]);
   415→
   416→  const handleChange: ChangeEventHandler<HTMLInputElement> = (event) => {
   417→    if (event.currentTarget.files) {
   418→      add(event.currentTarget.files);
   419→    }
   420→  };
   421→
   422→  const handleSubmit: FormEventHandler<HTMLFormElement> = (event) => {
   423→    event.preventDefault();
   424→
   425→    const files: FileUIPart[] = items.map(({ ...item }) => ({
   426→      ...item,
   427→    }));
   428→
   429→    onSubmit({ text: event.currentTarget.message.value, files }, event);
   430→  };
   431→
   432→  const ctx = useMemo<AttachmentsContext>(
   433→    () => ({
   434→      files: items.map((item) => ({ ...item, id: item.id })),
   435→      add,
   436→      remove,
   437→      clear,
   438→      openFileDialog,
   439→      fileInputRef: inputRef,
   440→    }),
   441→    [items, add, remove, clear, openFileDialog]
   442→  );
   443→
   444→  // Conditional rendering based on variant
   445→  if (variant === "glassmorphism") {
   446→    return (
   447→      <AttachmentsContext.Provider value={ctx}>
   448→        <span aria-hidden="true" className="hidden" ref={anchorRef} />
   449→        <input
   450→          accept={accept}
   451→          className="hidden"
   452→          multiple={multiple}
   453→          onChange={handleChange}
   454→          ref={inputRef}
   455→          type="file"
   456→        />
   457→        <form
   458→          onSubmit={handleSubmit}
   459→          className={className}
   460→          {...props}
   461→        >
   462→          {/* Glassmorphism variant renders children directly - no default styling */}
   463→        </form>
   464→      </AttachmentsContext.Provider>
   465→    );
   466→  }
   467→
   468→  // Default variant
   469→  return (
   470→    <AttachmentsContext.Provider value={ctx}>
   471→      <span aria-hidden="true" className="hidden" ref={anchorRef} />
   472→      <input
   473→        accept={accept}
   474→        className="hidden"
   475→        multiple={multiple}
   476→        onChange={handleChange}
   477→        ref={inputRef}
   478→        type="file"
   479→      />
   480→      <form
   481→        className={cn(
   482→          "w-full divide-y overflow-hidden rounded-lg border border-gray-200/50 bg-white/80 shadow-none",
   483→          className
   484→        )}
   485→        onSubmit={handleSubmit}
   486→        {...props}
   487→      />
   488→    </AttachmentsContext.Provider>
   489→  );
   490→};
   491→
