   501→export type PromptInputTextareaProps = ComponentProps<typeof Textarea>;
   502→
   503→export const PromptInputTextarea = ({
   504→  onChange,
   505→  className,
   506→  placeholder = "What would you like to know?",
   507→  ...props
   508→}: PromptInputTextareaProps) => {
   509→  const handleKeyDown: KeyboardEventHandler<HTMLTextAreaElement> = (e) => {
   510→    if (e.key === "Enter") {
   511→      // Don't submit if IME composition is in progress
   512→      if (e.nativeEvent.isComposing) {
   513→        return;
   514→      }
   515→
   516→      if (e.shiftKey) {
   517→        // Allow newline
   518→        return;
   519→      }
   520→
   521→      // Submit on Enter (without Shift)
   522→      e.preventDefault();
   523→      const form = e.currentTarget.form;
   524→      if (form) {
   525→        form.requestSubmit();
   526→      }
   527→    }
   528→  };
   529→
   530→  return (
   531→    <Textarea
   532→      className={cn(
   533→        "w-full resize-none rounded-none border-none p-3 shadow-none outline-none ring-0",
   534→        "field-sizing-content bg-transparent dark:bg-transparent",
   535→        "max-h-48 min-h-16",
   536→        "focus-visible:ring-0",
   537→        // Placeholder styling
   538→        "placeholder:text-placeholder placeholder:font-normal",
   539→        className
   540→      )}
   541→      style={{ lineHeight: '140%' }}
   542→      name="message"
   543→      onChange={(e) => {
   544→        onChange?.(e);
   545→      }}
   546→      onKeyDown={handleKeyDown}
   547→      placeholder={placeholder}
   548→      {...props}
   549→    />
   550→  );
   551→};
