   705→    )}
   706→    {...props}
   707→  />
   708→);
   709→
   710→export type PromptInputModelSelectContentProps = ComponentProps<
   711→  typeof SelectContent
   712→>;
   713→
   714→export const PromptInputModelSelectContent = ({
   715→  className,
   716→  ...props
   717→}: PromptInputModelSelectContentProps) => (
   718→  <SelectContent className={cn(className)} {...props} />
   719→);
   720→
   721→export type PromptInputModelSelectItemProps = ComponentProps<typeof SelectItem>;
   722→
   723→export const PromptInputModelSelectItem = ({
   724→  className,
   725→  ...props
   726→}: PromptInputModelSelectItemProps) => (
   727→  <SelectItem className={cn(className)} {...props} />
   728→);
