   740→
   741→// ============================================================================
   742→// Glassmorphism Variant Components
   743→// ============================================================================
   744→
   745→/**
   746→ * GradientBorderContainer - Creates pill-shaped container with gradient border
   747→ * Uses pseudo-element technique: outer div with gradient bg + 1px padding creates border effect
   748→ */
   749→interface GradientBorderContainerProps {
   750→  children: React.ReactNode;
   751→  className?: string;
   752→  maxWidth?: number;
   753→}
   754→
   755→export const GradientBorderContainer = ({
   756→  children,
   757→  className,
   758→  maxWidth = 900
   759→}: GradientBorderContainerProps) => (
   760→  <div
   761→    className={cn("mx-auto w-full", className)}
   762→    style={{ maxWidth: `${maxWidth}px` }}
   763→  >
   764→    {/* Outer container: gradient background shows through as 1px border */}
   765→    <div className="relative rounded-[40px] p-[1px]" style={{ background: 'var(--gradient-brand)' }}>
   766→      {/* Inner container: solid white background */}
   767→      <div className={cn(
   768→        "relative rounded-[40px]",
   769→        // Solid white background
   770→        "bg-white",
   771→        // Figma shadow spec
   772→        "shadow-[0_4px_16px_0_rgba(0,0,0,0.15)]"
   773→      )}>
   774→        {children}
   775→      </div>
   776→    </div>
   777→  </div>
   778→);
   779→
   780→/**
   781→ * GradientPlaceholderInput - Input with gradient placeholder text
   782→ * Uses overlay technique: visual gradient div + real placeholder for accessibility
   783→ */
   784→interface GradientPlaceholderInputProps {
   785→  value?: string;
   786→  onChange?: (value: string) => void;
   787→  onFocus?: () => void;
   788→  onBlur?: () => void;
   789→  placeholder?: string;
   790→  "aria-label"?: string;
   791→  className?: string;
   792→  name?: string;
   793→}
   794→
   795→export const GradientPlaceholderInput = ({
   796→  value: controlledValue,
   797→  onChange,
   798→  onFocus,
   799→  onBlur,
   800→  placeholder = "Ask me anything...",
   801→  "aria-label": ariaLabel = "Message input",
   802→  className,
   803→  name = "message"
   804→}: GradientPlaceholderInputProps) => {
   805→  const [internalValue, setInternalValue] = useState("");
   806→  const [isFocused, setIsFocused] = useState(false);
   807→
   808→  const value = controlledValue ?? internalValue;
   809→  const showGradientPlaceholder = !value && !isFocused;
   810→
   811→  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
   812→    const newValue = e.target.value;
   813→    if (controlledValue === undefined) {
   814→      setInternalValue(newValue);
   815→    }
   816→    onChange?.(newValue);
   817→  };
   818→
   819→  const handleFocus = () => {
   820→    setIsFocused(true);
   821→    onFocus?.();
   822→  };
   823→
   824→  const handleBlur = () => {
   825→    setIsFocused(false);
   826→    onBlur?.();
   827→  };
   828→
   829→  return (
   830→    <div className={cn("relative flex-1 min-w-0", className)}>
   831→      {/* Visual gradient placeholder (hidden from screen readers) */}
   832→      {showGradientPlaceholder && (
   833→        <div
   834→          className={cn(
   835→            "absolute inset-0 pointer-events-none",
   836→            "flex items-center px-4",
   837→            "text-base font-normal"
   838→          )}
   839→          style={{
   840→            lineHeight: '140%',
   841→            background: 'var(--gradient-brand)',
   842→            WebkitBackgroundClip: 'text',
   843→            backgroundClip: 'text',
   844→            WebkitTextFillColor: 'transparent',
   845→            color: 'transparent'
   846→          }}
   847→          aria-hidden="true"
   848→        >
   849→          {placeholder}
   850→        </div>
   851→      )}
   852→
   853→      {/* Actual input with standard placeholder for accessibility */}
   854→      <input
   855→        type="text"
   856→        name={name}
   857→        value={value}
   858→        onChange={handleChange}
   859→        onFocus={handleFocus}
   860→        onBlur={handleBlur}
   861→        placeholder={placeholder}
   862→        aria-label={ariaLabel}
   863→        className={cn(
   864→          "w-full h-full bg-transparent",
   865→          "px-4 py-0",
   866→          "text-base font-normal text-black",
   867→          "focus:outline-none",
   868→          // Hide native placeholder visually but keep for accessibility
   869→          "placeholder:text-transparent",
   870→          // Browser autofill styling
   871→          "autofill:bg-transparent"
   872→        )}
   873→        style={{ lineHeight: '140%' }}
   874→      />
   875→    </div>
   876→  );
   877→};
   878→
   879→/**
   880→ * IconButton - Circular icon button for glassmorphism variant
   881→ * Used for plus, microphone, and other icon actions
   882→ */
   883→interface IconButtonProps extends Omit<ComponentProps<"button">, "children"> {
   884→  icon: React.ReactNode;
   885→  "aria-label": string;
   886→  variant?: "default" | "gradient";
   887→}
   888→
   889→export const IconButton = ({
   890→  icon,
