   892→  variant = "default",
   893→  className,
   894→  type = "button",
   895→  ...props
   896→}: IconButtonProps) => (
   897→  <button
   898→    type={type}
   899→    aria-label={ariaLabel}
   900→    className={cn(
   901→      // Base circular button
   902→      "w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0",
   903→      "transition-all duration-200",
   904→      // Variants
   905→      variant === "default" && [
   906→        "bg-gray-100 hover:bg-gray-200",
   907→        "text-gray-600"
   908→      ],
   909→      variant === "gradient" && [
   910→        "text-white hover:shadow-lg hover:scale-105"
   911→      ],
   912→      className
   913→    )}
   914→    style={variant === "gradient" ? { background: 'var(--gradient-brand)' } : undefined}
   915→    {...props}
   916→  >
   917→    {icon}
   918→  </button>
   919→);
   920→
   921→/**
   922→ * GradientSubmitButton - Submit button with gradient background and arrow icon
   923→ * Glassmorphism variant submit button
   924→ */
   925→interface GradientSubmitButtonProps extends Omit<ComponentProps<"button">, "children" | "type"> {
