// ============================================================================
// Glassmorphism Variant Components
// ============================================================================

/**
 * GradientBorderContainer - Creates pill-shaped container with gradient border
 * Uses pseudo-element technique: outer div with gradient bg + 1px padding creates border effect
 */
interface GradientBorderContainerProps {
  children: React.ReactNode;
  className?: string;
  maxWidth?: number;
}

export const GradientBorderContainer = ({
  children,
  className,
  maxWidth = 900
}: GradientBorderContainerProps) => (
  <div
    className={cn("mx-auto w-full", className)}
    style={{ maxWidth: `${maxWidth}px` }}
  >
    {/* Outer container: gradient background shows through as 1px border */}
    <div className="relative rounded-[40px] p-[1px]" style={{ background: 'var(--gradient-brand)' }}>
      {/* Inner container: solid white background */}
      <div className={cn(
        "relative rounded-[40px]",
        // Solid white background
        "bg-white",
        // Figma shadow spec
        "shadow-[0_4px_16px_0_rgba(0,0,0,0.15)]"
      )}>
        {children}
      </div>
    </div>
  </div>
);

/**
 * GradientPlaceholderInput - Input with gradient placeholder text
 * Uses overlay technique: visual gradient div + real placeholder for accessibility
 */
interface GradientPlaceholderInputProps {
  value?: string;
  onChange?: (value: string) => void;
  onFocus?: () => void;
  onBlur?: () => void;
  placeholder?: string;
  "aria-label"?: string;
  className?: string;
  name?: string;
}

export const GradientPlaceholderInput = ({
  value: controlledValue,
  onChange,
  onFocus,
  onBlur,
  placeholder = "Ask me anything...",
  "aria-label": ariaLabel = "Message input",
  className,
  name = "message"
}: GradientPlaceholderInputProps) => {
  const [internalValue, setInternalValue] = useState("");
  const [isFocused, setIsFocused] = useState(false);

  const value = controlledValue ?? internalValue;
  const showGradientPlaceholder = !value && !isFocused;

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    if (controlledValue === undefined) {
      setInternalValue(newValue);
    }
    onChange?.(newValue);
  };

  const handleFocus = () => {
    setIsFocused(true);
    onFocus?.();
  };

  const handleBlur = () => {
    setIsFocused(false);
    onBlur?.();
  };

  return (
    <div className={cn("relative flex-1 min-w-0", className)}>
      {/* Visual gradient placeholder (hidden from screen readers) */}
      {showGradientPlaceholder && (
        <div
          className={cn(
            "absolute inset-0 pointer-events-none",
            "flex items-center px-4",
            "text-base font-normal"
          )}
          style={{
            lineHeight: '140%',
            background: 'var(--gradient-brand)',
            WebkitBackgroundClip: 'text',
            backgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            color: 'transparent'
          }}
          aria-hidden="true"
        >
          {placeholder}
        </div>
      )}

      {/* Actual input with standard placeholder for accessibility */}
      <input
        type="text"
        name={name}
        value={value}
        onChange={handleChange}
        onFocus={handleFocus}
        onBlur={handleBlur}
        placeholder={placeholder}
        aria-label={ariaLabel}
        className={cn(
          "w-full h-full bg-transparent",
          "px-4 py-0",
          "text-base font-normal text-black",
          "focus:outline-none",
          // Hide native placeholder visually but keep for accessibility
          "placeholder:text-transparent",
          // Browser autofill styling
          "autofill:bg-transparent"
        )}
        style={{ lineHeight: '140%' }}
      />
    </div>
  );
};

/**
 * IconButton - Circular icon button for glassmorphism variant
 * Used for plus, microphone, and other icon actions
 */
interface IconButtonProps extends Omit<ComponentProps<"button">, "children"> {
  icon: React.ReactNode;
  "aria-label": string;
  variant?: "default" | "gradient";
}

export const IconButton = ({
  icon,
  "aria-label": ariaLabel,
  variant = "default",
  className,
  type = "button",
  ...props
}: IconButtonProps) => (
  <button
    type={type}
    aria-label={ariaLabel}
    className={cn(
      // Base circular button
      "w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0",
      "transition-all duration-200",
      // Variants
      variant === "default" && [
        "bg-gray-100 hover:bg-gray-200",
        "text-gray-600"
      ],
      variant === "gradient" && [
        "text-white hover:shadow-lg hover:scale-105"
      ],
      className
    )}
    style={variant === "gradient" ? { background: 'var(--gradient-brand)' } : undefined}
    {...props}
  >
    {icon}
  </button>
);

/**
 * GradientSubmitButton - Submit button with gradient background and arrow icon
 * Glassmorphism variant submit button
 */
interface GradientSubmitButtonProps extends Omit<ComponentProps<"button">, "children" | "type"> {
  disabled?: boolean;
  "aria-label"?: string;
}

export const GradientSubmitButton = ({
  disabled,
  "aria-label": ariaLabel = "Send message",
  className,
  ...props
}: GradientSubmitButtonProps) => (
  <button
    type="submit"
    disabled={disabled}
    aria-label={ariaLabel}
    className={cn(
      "w-10 h-10 rounded-full flex-shrink-0",
      "flex items-center justify-center",
      "text-white transition-all duration-200",
      "hover:shadow-lg hover:scale-105",
      "disabled:opacity-50 disabled:cursor-not-allowed",
      "disabled:hover:scale-100 disabled:hover:shadow-none",
      className
    )}
    style={{ background: 'var(--gradient-brand)' }}
    {...props}
  >
    {/* Arrow right icon */}
    <svg
      width="20"
      height="20"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M5 12h14M12 5l7 7-7 7" />
    </svg>
  </button>
);

