      {/* Navigation Between Widgets */}
      <div className="flex items-center justify-between border-y py-4">
        <div>
          {widgetData.navigation.previous ? (
            <Button asChild variant="outline">
              <Link href={`/admin/components/widgets/${widgetData.navigation.previous}`}>
                <ChevronLeft className="h-4 w-4 mr-2" />
                <span className="capitalize">{widgetData.navigation.previous.replace(/-/g, " ")}</span>
              </Link>
            </Button>
          ) : (
            <Button variant="outline" disabled>
              <ChevronLeft className="h-4 w-4 mr-2" />
              Previous
            </Button>
          )}
        </div>

        <div className="text-sm text-muted-foreground">
          {widgetData.navigation.current} of {widgetData.navigation.total}
        </div>

        <div>
          {widgetData.navigation.next ? (
            <Button asChild variant="outline">
              <Link href={`/admin/components/widgets/${widgetData.navigation.next}`}>
                <span className="capitalize">{widgetData.navigation.next.replace(/-/g, " ")}</span>
                <ChevronRight className="h-4 w-4 ml-2" />
              </Link>
            </Button>
          ) : (
            <Button variant="outline" disabled>
              Next
              <ChevronRight className="h-4 w-4 ml-2" />
            </Button>
          )}
        </div>
      </div>

      {/* Widget Preview */}
      {DemoComponent ? (
        <ComponentPreview>
          <ComponentPreview.Demo>
            <DemoComponent />
          </ComponentPreview.Demo>
          <ComponentPreview.Code code={widgetData.code} />
        </ComponentPreview>
      ) : (
        <Alert>
          <AlertDescription>
            Preview not available for this widget yet. Please check back later.
          </AlertDescription>
        </Alert>
      )}

      {/* Back Button */}
      <div className="pt-6">
        <Button asChild variant="ghost">
          <Link href="/admin/components/widgets">
            <ChevronLeft className="h-4 w-4 mr-2" />
            Back to All Widgets
          </Link>
        </Button>
      </div>
    </div>
  );
}

"use client";

import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";
import { ComponentPreview } from "@/components/ComponentPreview";
import { WIDGET_DEMOS } from "@/components/component-previews/widget-demos";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";
import { ChevronLeft, ChevronRight, AlertCircle } from "lucide-react";
import Link from "next/link";
import { use } from "react";

interface PageProps {
  params: Promise<{ widget: string }>;
}

export default function WidgetPreviewPage({ params }: PageProps) {
  const { widget } = use(params);
  const widgetData = useQuery(api.componentPreviews.getWidgetComponentPreview, {
    widgetName: widget,
  });

  // Loading state
  if (widgetData === undefined) {
    return (
      <div className="p-8 space-y-6">
        {/* Header Skeleton */}
        <div className="space-y-2">
          <Skeleton className="h-10 w-64" />
          <Skeleton className="h-4 w-96" />
        </div>

        {/* Navigation Skeleton */}
        <div className="flex items-center justify-between">
          <Skeleton className="h-10 w-32" />
          <Skeleton className="h-6 w-24" />
          <Skeleton className="h-10 w-32" />
        </div>

        {/* Preview Skeleton */}
        <Skeleton className="h-96 w-full" />
      </div>
    );
  }

  // Error state - widget not found
  if (widgetData === null) {
    return (
      <div className="p-8">
        <Alert variant="destructive">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            Widget "{widget}" not found. Please check the widget name and try again.
          </AlertDescription>
        </Alert>
        <div className="mt-6">
          <Button asChild variant="outline">
            <Link href="/admin/components/widgets">
              <ChevronLeft className="h-4 w-4 mr-2" />
              Back to Widgets
            </Link>
          </Button>
        </div>
      </div>
    );
  }

  // Get the demo component
  const DemoComponent = WIDGET_DEMOS[widget];

  // Format category display name
  const categoryDisplay = widgetData.category
    .split("-")
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(" ");

  return (
    <div className="p-8 space-y-6">
      {/* Breadcrumb */}
      <div className="flex items-center text-sm text-muted-foreground space-x-2">
        <Link href="/admin" className="hover:text-foreground">Admin</Link>
        <span>/</span>
        <Link href="/admin/components" className="hover:text-foreground">Components</Link>
        <span>/</span>
        <Link href="/admin/components/widgets" className="hover:text-foreground">
          Widget Components
        </Link>
        <span>/</span>
        <span className="text-foreground font-medium capitalize">{widget.replace(/-/g, " ")}</span>
      </div>

      {/* Header */}
      <div className="space-y-2">
        <div className="flex items-center gap-3">
          <h1 className="text-3xl font-bold capitalize">{widget.replace(/-/g, " ")}</h1>
          <Badge variant="secondary">{categoryDisplay}</Badge>
        </div>
        <p className="text-muted-foreground">{widgetData.description}</p>
      </div>

      {/* Navigation Between Widgets */}
      <div className="flex items-center justify-between border-y py-4">
        <div>
          {widgetData.navigation.previous ? (
            <Button asChild variant="outline">
              <Link href={`/admin/components/widgets/${widgetData.navigation.previous}`}>
                <ChevronLeft className="h-4 w-4 mr-2" />
                <span className="capitalize">{widgetData.navigation.previous.replace(/-/g, " ")}</span>
              </Link>
            </Button>
          ) : (
            <Button variant="outline" disabled>
              <ChevronLeft className="h-4 w-4 mr-2" />
              Previous
            </Button>
          )}
        </div>

        <div className="text-sm text-muted-foreground">
          {widgetData.navigation.current} of {widgetData.navigation.total}
        </div>

        <div>
          {widgetData.navigation.next ? (
            <Button asChild variant="outline">
              <Link href={`/admin/components/widgets/${widgetData.navigation.next}`}>
                <span className="capitalize">{widgetData.navigation.next.replace(/-/g, " ")}</span>
                <ChevronRight className="h-4 w-4 ml-2" />
              </Link>
            </Button>
          ) : (
            <Button variant="outline" disabled>
              Next
              <ChevronRight className="h-4 w-4 ml-2" />
            </Button>
          )}
        </div>
      </div>

      {/* Widget Preview */}
      {DemoComponent ? (
        <ComponentPreview>
          <ComponentPreview.Demo>
            <DemoComponent />
          </ComponentPreview.Demo>
          <ComponentPreview.Code code={widgetData.code} />
        </ComponentPreview>
      ) : (
        <Alert>
          <AlertDescription>
            Preview not available for this widget yet. Please check back later.
          </AlertDescription>
        </Alert>
      )}

      {/* Back Button */}
      <div className="pt-6">
        <Button asChild variant="ghost">
          <Link href="/admin/components/widgets">
            <ChevronLeft className="h-4 w-4 mr-2" />
            Back to Widget Components
          </Link>
        </Button>
      </div>
    </div>
  );
}

"use client";

import { useState } from "react";
import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Search, Package, Eye } from "lucide-react";
import Link from "next/link";

export default function WidgetComponentsPage() {
  const components = useQuery(api.components.getWidgetComponentsList);
  const [searchQuery, setSearchQuery] = useState("");

  const isLoading = components === undefined;

  // Filter components by search query across all categories
  const filterComponents = (categoryComponents: any[]) => {
    if (!searchQuery) return categoryComponents;
    const query = searchQuery.toLowerCase();
    return categoryComponents.filter((component) =>
      component.name.toLowerCase().includes(query) ||
      component.path.toLowerCase().includes(query) ||
      component.description?.toLowerCase().includes(query)
    );
  };

  // Get filtered counts for each category
  const getFilteredCount = (categoryKey: string) => {
    if (!components) return 0;
    return filterComponents(components[categoryKey as keyof typeof components] || []).length;
  };

  return (
    <div className="p-8 space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Widget Components</h1>
        <p className="text-gray-600 mt-2">
          Browse custom widget components organized by category.
        </p>
      </div>

      {/* Search */}
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
          placeholder="Search across all widget components..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="pl-10"
        />
      </div>

      {/* Stats */}
      {!isLoading && components && (
        <div className="flex gap-4 text-sm text-gray-600">
          <span>
            Total Widgets: <strong>
              {(components.icons?.length || 0) +
                (components.animations?.length || 0) +
                (components["ai-elements"]?.length || 0) +
                (components["ask-anything"]?.length || 0)}
            </strong>
          </span>
          {searchQuery && (
            <>
              <span>•</span>
              <span>
                Filtered: <strong>
                  {getFilteredCount("icons") +
                    getFilteredCount("animations") +
                    getFilteredCount("ai-elements") +
                    getFilteredCount("ask-anything")}
                </strong>
              </span>
            </>
          )}
        </div>
      )}

      {/* Widget Tabs by Category */}
      <Tabs defaultValue="icons" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="icons">
            Icons {!isLoading && `(${getFilteredCount("icons")})`}
          </TabsTrigger>
          <TabsTrigger value="animations">
            Animations {!isLoading && `(${getFilteredCount("animations")})`}
          </TabsTrigger>
          <TabsTrigger value="ai-elements">
            AI Elements {!isLoading && `(${getFilteredCount("ai-elements")})`}
          </TabsTrigger>
          <TabsTrigger value="ask-anything">
            Ask Anything {!isLoading && `(${getFilteredCount("ask-anything")})`}
          </TabsTrigger>
        </TabsList>

        {/* Icons Tab */}
        <TabsContent value="icons" className="space-y-4">
          <ScrollArea className="h-[600px] rounded-md border p-4">
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Array.from({ length: 3 }).map((_, i) => (
                  <Card key={i}>
                    <CardHeader>
                      <Skeleton className="h-5 w-32" />
                      <Skeleton className="h-4 w-full" />
                    </CardHeader>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filterComponents(components?.icons || []).map((component) => (
                  <Card key={component.name} className="hover:shadow-md transition-shadow">
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2 text-base">
                        <Package className="h-4 w-4" />
                        <Link
                          href={`/admin/components/widgets/${component.name}`}
                          className="hover:text-blue-600 hover:underline"
                        >
                          {component.name}
                        </Link>
                      </CardTitle>
                      <CardDescription className="text-xs">
                        {component.description || "Icon component"}
                      </CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <Badge variant="secondary" className="text-xs">
                          {component.category}
                        </Badge>
                        <p className="text-xs text-gray-500 font-mono truncate">
                          {component.path}
                        </p>
                        <Button asChild variant="outline" size="sm" className="w-full">
                          <Link href={`/admin/components/widgets/${component.name}`}>
                            <Eye className="h-4 w-4 mr-2" />
                            View Preview
                          </Link>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
                {filterComponents(components?.icons || []).length === 0 && (
                  <p className="text-center text-gray-500 col-span-full py-8">
                    {searchQuery ? "No icons match your search." : "No icon components found."}
                  </p>
                )}
              </div>
            )}
          </ScrollArea>
        </TabsContent>

        {/* Animations Tab */}
        <TabsContent value="animations" className="space-y-4">
          <ScrollArea className="h-[600px] rounded-md border p-4">
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Array.from({ length: 1 }).map((_, i) => (
                  <Card key={i}>
                    <CardHeader>
                      <Skeleton className="h-5 w-32" />
                      <Skeleton className="h-4 w-full" />
                    </CardHeader>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filterComponents(components?.animations || []).map((component) => (
                  <Card key={component.name} className="hover:shadow-md transition-shadow">
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2 text-base">
                        <Package className="h-4 w-4" />
                        <Link
                          href={`/admin/components/widgets/${component.name}`}
                          className="hover:text-blue-600 hover:underline"
                        >
                          {component.name}
                        </Link>
                      </CardTitle>
                      <CardDescription className="text-xs">
                        {component.description || "Animation component"}
                      </CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <Badge variant="secondary" className="text-xs">
                          {component.category}
                        </Badge>
                        <p className="text-xs text-gray-500 font-mono truncate">
                          {component.path}
                        </p>
                        <Button asChild variant="outline" size="sm" className="w-full">
                          <Link href={`/admin/components/widgets/${component.name}`}>
                            <Eye className="h-4 w-4 mr-2" />
                            View Preview
                          </Link>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
                {filterComponents(components?.animations || []).length === 0 && (
                  <p className="text-center text-gray-500 col-span-full py-8">
                    {searchQuery ? "No animations match your search." : "No animation components found."}
                  </p>
                )}
              </div>
            )}
          </ScrollArea>
        </TabsContent>

        {/* AI Elements Tab */}
        <TabsContent value="ai-elements" className="space-y-4">
          <ScrollArea className="h-[600px] rounded-md border p-4">
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Array.from({ length: 6 }).map((_, i) => (
                  <Card key={i}>
                    <CardHeader>
                      <Skeleton className="h-5 w-32" />
                      <Skeleton className="h-4 w-full" />
                    </CardHeader>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filterComponents(components?.["ai-elements"] || []).map((component) => (
                  <Card key={component.name} className="hover:shadow-md transition-shadow">
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2 text-base">
                        <Package className="h-4 w-4" />
                        <Link
                          href={`/admin/components/widgets/${component.name}`}
                          className="hover:text-blue-600 hover:underline"
                        >
                          {component.name}
                        </Link>
                      </CardTitle>
                      <CardDescription className="text-xs">
                        {component.description || "AI element component"}
                      </CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <Badge variant="secondary" className="text-xs">
                          {component.category}
                        </Badge>
                        <p className="text-xs text-gray-500 font-mono truncate">
                          {component.path}
                        </p>
                        <Button asChild variant="outline" size="sm" className="w-full">
                          <Link href={`/admin/components/widgets/${component.name}`}>
                            <Eye className="h-4 w-4 mr-2" />
                            View Preview
                          </Link>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
                {filterComponents(components?.["ai-elements"] || []).length === 0 && (
                  <p className="text-center text-gray-500 col-span-full py-8">
                    {searchQuery ? "No AI elements match your search." : "No AI element components found."}
                  </p>
                )}
              </div>
            )}
          </ScrollArea>
        </TabsContent>

        {/* Ask Anything Tab */}
        <TabsContent value="ask-anything" className="space-y-4">
          <ScrollArea className="h-[600px] rounded-md border p-4">
            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Array.from({ length: 1 }).map((_, i) => (
                  <Card key={i}>
                    <CardHeader>
                      <Skeleton className="h-5 w-32" />
                      <Skeleton className="h-4 w-full" />
                    </CardHeader>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {filterComponents(components?.["ask-anything"] || []).map((component) => (
                  <Card key={component.name} className="hover:shadow-md transition-shadow">
                    <CardHeader>
                      <CardTitle className="flex items-center gap-2 text-base">
                        <Package className="h-4 w-4" />
                        <Link
                          href={`/admin/components/widgets/${component.name}`}
                          className="hover:text-blue-600 hover:underline"
                        >
                          {component.name}
                        </Link>
                      </CardTitle>
                      <CardDescription className="text-xs">
                        {component.description || "Ask Anything component"}
                      </CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <Badge variant="secondary" className="text-xs">
                          {component.category}
                        </Badge>
                        <p className="text-xs text-gray-500 font-mono truncate">
                          {component.path}
                        </p>
                        <Button asChild variant="outline" size="sm" className="w-full">
                          <Link href={`/admin/components/widgets/${component.name}`}>
                            <Eye className="h-4 w-4 mr-2" />
                            View Preview
                          </Link>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
                {filterComponents(components?.["ask-anything"] || []).length === 0 && (
                  <p className="text-center text-gray-500 col-span-full py-8">
                    {searchQuery ? "No ask-anything components match your search." : "No ask-anything components found."}
                  </p>
                )}
              </div>
            )}
          </ScrollArea>
        </TabsContent>
      </Tabs>
    </div>
  );
